parameters.jsCode:
// Handle both webhook (body) and test panel (direct) data
const body = $input.item.json.body || $input.item.json;

// Generate unique IDs
const onboardingId = `ONB-${new Date().getFullYear()}-${String(Math.floor(Math.random() * 99999)).padStart(5, '0')}`;
const employeeId = `EMP-${new Date().getFullYear()}-${String(Math.floor(Math.random() * 99999)).padStart(5, '0')}`;

// Generate corporate email
const firstName = body.first_name.toLowerCase().trim();
const lastName = body.last_name.toLowerCase().trim();
const corporateEmail = `${firstName}.${lastName}@aigs-demo.com`;

// Generate temporary password
const tempPassword = `Welcome${new Date().getFullYear()}!${Math.random().toString(36).substring(2, 6).toUpperCase()}`;

// Generate username variations
const slackUsername = `${firstName}.${lastName}`;
const jiraUsername = `${firstName[0]}${lastName}`.toLowerCase();

// Calculate key dates
const startDate = new Date(body.start_date);
const day30 = new Date(startDate); day30.setDate(day30.getDate() + 30);
const day60 = new Date(startDate); day60.setDate(day60.getDate() + 60);
const day90 = new Date(startDate); day90.setDate(day90.getDate() + 90);

// Generate Slack channel name
const slackChannel = `onboarding-${firstName}-${lastName}`.toLowerCase();

return {
  onboarding_id: onboardingId,
  employee_id: employeeId,
  first_name: body.first_name,
  last_name: body.last_name,
  full_name: `${body.first_name} ${body.last_name}`,
  personal_email: body.personal_email,
  corporate_email: corporateEmail,
  temp_password: tempPassword,
  department: body.department,
  title: body.title || 'Team Member',
  manager_name: body.manager_name || 'TBD',
  manager_email: body.manager_email || 'manager@aigs-demo.com',
  start_date: body.start_date,
  location: body.location || 'Remote',
  equipment_needed: body.equipment_needed || ['laptop'],
  slack_username: slackUsername,
  slack_channel: slackChannel,
  jira_username: jiraUsername,
  milestones: {
    day_1: body.start_date,
    day_30: day30.toISOString().split('T')[0],
    day_60: day60.toISOString().split('T')[0],
    day_90: day90.toISOString().split('T')[0]
  },
  started_at: new Date().toISOString()
};

// Pass through all data
const data = $('Initialize Onboarding Data').item.json;
return { ...data };

// Simulate Google Workspace account creation
const data = $('Initialize Onboarding Data').item.json;

// Simulate API delay (2 seconds)
await new Promise(resolve => setTimeout(resolve, 2000));

return {
  ...data,
  google_workspace_result: {
    status: 'created',
    email: data.corporate_email,
    temp_password: data.temp_password,
    created_at: new Date().toISOString(),
    services_enabled: ['Gmail', 'Drive', 'Calendar', 'Meet']
  }
};

