// Combine all results and prepare for document phase
const initData = $('Initialize Onboarding Data').item.json;

return {
  ...initData,
  accounts_complete: true
};

// Generate Welcome Packet content in sections for Notion
const data = $('Initialize Onboarding Data').item.json;

// Section 1: Employee Info (for Notion properties or header)
const employeeInfo = {
  name: data.full_name,
  employee_id: data.employee_id,
  department: data.department,
  title: data.title,
  start_date: data.start_date,
  location: data.location,
  manager: data.manager_name,
  corporate_email: data.corporate_email
};

// Section 2: Account Credentials (keep under 2000 chars)
const credentialsBlock = `**Corporate Email:** ${data.corporate_email}
**Temporary Password:** ${data.temp_password}
**Slack Username:** ${data.slack_username}
**Jira Username:** ${data.jira_username}

 *Please change your password upon first login!*`;

// Section 3: First Week Schedule
const scheduleBlock = `**Day 1 - 9:00 AM**  Welcome & Orientation (HR)
**Day 1 - 1:00 PM**  IT Setup & Security Training
**Day 1 - 4:00 PM**  1:1 with ${data.manager_name}
**Day 2 - 10:00 AM**  Team Introduction
**Day 5 - 2:00 PM**  Week 1 Check-in`;

// Section 4: Key Milestones
const milestonesBlock = `**30-Day Review:** ${data.milestones.day_30}
**60-Day Review:** ${data.milestones.day_60}
**90-Day Review:** ${data.milestones.day_90}`;

// Section 5: Equipment
const equipmentList = data.equipment_needed.map(item => ` ${item}`).join('\n');
const equipmentBlock = `${equipmentList}

IT will reach out to coordinate delivery/setup.`;

// Section 6: Onboarding Checklist (for to-do items)
const checklistItems = [
  "Complete new hire paperwork",
  "Set up corporate email",
  "Change temporary password",
  "Join Slack workspace",
  "Complete security training",
  "Set up 2FA on all accounts",
  "Review employee handbook",
  "Meet with manager",
  "Meet team members",
  "Set up development environment",
  "Review first project assignment",
  "Complete 30-day check-in"
];

// Section 7: Quick Links (placeholder URLs)
const linksBlock = ` [Employee Handbook](https://company.notion.so/handbook)
 [IT Support Portal](https://company.notion.so/it-support)
 [Benefits Information](https://company.notion.so/benefits)
 [Slack Workspace](https://aigs-demo.slack.com)`;

// Section 8: Welcome Message
const welcomeMessage = `Welcome to AiGS, ${data.first_name}! We're thrilled to have you joining the ${data.department} team. Your manager ${data.manager_name} and the entire team are looking forward to working with you.`;

// Full HTML for PDF/email (still available if needed)
const fullHtml = `<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 40px; }
    .header { background: #1a365d; color: white; padding: 30px; text-align: center; border-radius: 8px; }
    h2 { color: #1a365d; border-bottom: 2px solid #3182ce; padding-bottom: 10px; }
    table { width: 100%; border-collapse: collapse; margin: 15px 0; }
    td { padding: 12px; border-bottom: 1px solid #e2e8f0; }
    td:first-child { font-weight: bold; width: 40%; }
    .highlight { background: #ebf8ff; padding: 20px; border-radius: 8px; border-left: 4px solid #3182ce; }
  </style>
</head>
<body>
  <div class="header"><h1>Welcome to AiGS!</h1></div>
  <h2>Your Information</h2>
  <table>
    <tr><td>Name</td><td>${data.full_name}</td></tr>
    <tr><td>Employee ID</td><td>${data.employee_id}</td></tr>
    <tr><td>Department</td><td>${data.department}</td></tr>
    <tr><td>Start Date</td><td>${data.start_date}</td></tr>
  </table>
</body>
</html>`;

return {
  ...data,

  // Sectioned content for Notion
  notion_sections: {
    welcome_message: welcomeMessage,
    credentials: credentialsBlock,
    schedule: scheduleBlock,
    milestones: milestonesBlock,
    equipment: equipmentBlock,
    links: linksBlock
  },

  // Checklist as array (for Notion to-do blocks)
  checklist_items: checklistItems,

  // Employee info object (for Notion properties)
  employee_info: employeeInfo,

  // Full HTML still available for PDF/email
  welcome_html: fullHtml
};

