// ROBUST PARSER - handles multiple script formats
const script = items[0].json.output;

console.log('Parser started - script length:', script ? script.length : 'null');

if (!script) {
  return [{json: {error: 'No script content'}}];
}

// Clean up the script
const cleanScript = script.replace(/---/g, '').trim();
const sections = [];
const lines = cleanScript.split('\n');

let currentSection = null;
let currentContent = [];

lines.forEach((line, lineIndex) => {
  const trimmedLine = line.trim();

  // Multiple ways to detect INTRO
  const isIntro = trimmedLine === '**INTRO**' || 
                  trimmedLine === 'INTRO' || 
                  trimmedLine === '**INTRO:**' ||
                  trimmedLine === 'INTRODUCTION' ||
                  trimmedLine.toLowerCase().includes('intro');

  // Multiple ways to detect OUTRO  
  const isOutro = trimmedLine === '**OUTRO**' || 
                  trimmedLine === 'OUTRO' || 
                  trimmedLine === '**OUTRO:**' ||
                  trimmedLine === 'CONCLUSION' ||
                  trimmedLine.toLowerCase().includes('outro');

  // Multiple ways to detect CHAPTER
  const chapterMatch = trimmedLine.match(/\*?\*?CHAPTER\s*(\d+)[:\-\.]?\s*(.+?)?\*?\*?/i) ||
                       trimmedLine.match(/^(\d+)[\.\-\:]?\s*(.+)/i) ||
                       trimmedLine.match(/^Chapter\s*(\d+)[:\-\.]?\s*(.+)?/i);

  console.log(`Line ${lineIndex}: "${trimmedLine}" - Intro: ${isIntro}, Outro: ${isOutro}, Chapter: ${!!chapterMatch}`);

  if (isIntro) {
    // Save previous section
    if (currentSection) {
      sections.push({
        ...currentSection,
        text: currentContent.join('\n').trim()
      });
    }
    currentSection = {
      type: 'intro',
      number: 0,
      title: 'INTRO'
    };
    currentContent = [];

  } else if (isOutro) {
    // Save previous section
    if (currentSection) {
      sections.push({
        ...currentSection,
        text: currentContent.join('\n').trim()
      });
    }
    currentSection = {
      type: 'outro',
      number: 7,
      title: 'OUTRO'
    };
    currentContent = [];

  } else if (chapterMatch) {
    // Save previous section
    if (currentSection) {
      sections.push({
        ...currentSection,
        text: currentContent.join('\n').trim()
      });
    }

    const chapterNumber = Number(chapterMatch[1]);
    const chapterTitle = chapterMatch[2] ? chapterMatch[2].trim() : `Chapter ${chapterNumber}`;

    currentSection = {
      type: 'chapter',
      number: chapterNumber,
      title: chapterTitle
    };
    currentContent = [];

  } else {
    // Add line to current content (skip empty lines at start)
    if (currentSection && (currentContent.length > 0 || trimmedLine.length > 0)) {
      currentContent.push(line);
    }
  }
});

// Don't forget the last section
if (currentSection) {
  sections.push({
    ...currentSection,
    text: currentContent.join('\n').trim()
  });
}

console.log(`Parsed ${sections.length} sections:`, sections.map(s => `${s.type}-${s.number}`));

// Fallback: if no sections found, try paragraph-based splitting
if (sections.length === 0) {
  console.log('No sections found, trying paragraph splitting...');
  const paragraphs = cleanScript.split(/\n\n+/).filter(p => p.trim().length > 100);

  paragraphs.forEach((paragraph, index) => {
    sections.push({
      type: index === 0 ? 'intro' : index === paragraphs.length - 1 ? 'outro' : 'chapter',
      number: index,
      title: `Section ${index + 1}`,
      text: paragraph.trim()
    });
  });
}

console.log(`Final result: ${sections.length} sections`);
return sections.map(s => ({json: s}));

// Process ALL binary image data items
const results = [];

items.forEach((item, index) => {
  const imageData = item.binary;
  const jsonData = item.json;

  // Create a descriptive filename
  const chapterNum = jsonData.chapter_number || '0';
  const sceneNum = jsonData.scene_number || '1';
  const shotNum = jsonData.shot_number || '1';
  const shotType = jsonData.shot_type || 'shot';

  // Clean up shot type for filename (remove spaces, special chars)
  const cleanShotType = shotType
    .toLowerCase()
    .replace(/[^a-z0-9]/g, '_')
    .replace(/_+/g, '_')
    .replace(/^_|_$/g, '');

  // Create filename: ch1_sc2_sh3_wide_establishing_shot.png
  const filename = `ch${chapterNum}_sc${sceneNum}_sh${shotNum}_${cleanShotType}.png`;

  // Add this item to results
  results.push({
    json: {
      ...jsonData,
      filename: filename,
      mimeType: 'image/png'
    },
    binary: {
      data: imageData.data,
      fileName: filename,
      mimeType: 'image/png'
    }
  });
});

console.log(`Processed ${results.length} images`);
return results;

