// Process ALL chapters from the parse node
const allImagePrompts = [];

// Loop through each chapter
items.forEach((item, chapterIndex) => {
  const chapterText = item.json.text;
  const chapterNumber = item.json.number;
  const chapterTitle = item.json.title;
  const chapterType = item.json.type;

  // Skip if no text content
  if (!chapterText || chapterText.trim() === '') {
    return;
  }

  // Split on [SCENE] markers first, then fallback to paragraph breaks
  let scenes;
  if (chapterText.includes('[SCENE]')) {
    // Split on [SCENE] markers and filter out the markers themselves
    scenes = chapterText.split(/\[SCENE\][^\n]*\n?/).filter(scene => scene.trim().length > 50);
  } else {
    // Fallback: split on double line breaks for substantial chunks
    scenes = chapterText.split(/\n\n+/).filter(scene => scene.trim().length > 100);
  }

  // If still no good scenes, use whole chapter as one scene
  if (scenes.length === 0) {
    scenes = [chapterText];
  }

  // NOW scenes is defined - process each scene
  scenes.forEach((scene, sceneIndex) => {
    const cleanScene = scene.trim();

    // Only use scenes longer than 150 characters
    if (cleanScene.length > 150) {

      // For intro/outro: 2 shots per scene
      // For chapters: 3 shots per scene
      const shotSequence = chapterType === 'intro' || chapterType === 'outro' 
        ? ["wide establishing shot", "medium shot"]
        : ["wide establishing shot", "medium shot", "close-up detail"];

      shotSequence.forEach((shotType, shotIndex) => {
        allImagePrompts.push({
          chapter_type: chapterType,
          chapter_number: chapterNumber,
          chapter_title: chapterTitle,
          scene_number: sceneIndex + 1,
          shot_number: shotIndex + 1,
          shot_type: shotType,
          scene_text: cleanScene,
          full_chapter_context: chapterText,
          global_image_id: chapterNumber + "-" + (sceneIndex + 1) + "-" + (shotIndex + 1)
        });
      });
    }
  });
});

console.log("Created " + allImagePrompts.length + " total image prompts");
return allImagePrompts.map(s => ({json: s}));

