// Prepare payload for Publish & Distribute workflow
const articleHtml = $input.item.json.content[0].text;
const formData = $('Test').item.json;

return {
  json: {
    articleHtml: articleHtml,
    articleType: 'complaint',
    defendant: formData.Defendant_Name,
    caseNumber: formData.Case_Number,
    ctaType: 'case_tracker',
    pdfUrl: formData.pdfUrl || null,  // Complaint PDF should be embedded
    customInstructions: formData.Custom_Instructions || '',
    metaData: {
      defendant: formData.Defendant_Name,
      caseNumber: formData.Case_Number,
      articleType: 'complaint',
      hasPDF: !!formData.pdfUrl
    }
  }
};

