// Parse infographic JSON and prepare payload for Publish & Distribute
const articleHtml = $('Generate Settlement Article').item.json.content[0].text;
const infographicRaw = $input.item.json.content[0].text;
const formData = $('When Executed by Another Workflow').item.json;

// Parse infographic JSON (Claude might wrap it in markdown)
let infographic;
try {
  // Remove markdown code blocks if present
  const cleanJson = infographicRaw.replace(/```json\s*/gi, '').replace(/```\s*/g, '').trim();
  infographic = JSON.parse(cleanJson);
} catch (e) {
  // Fallback if parsing fails
  infographic = {
    infographicType: 'timeline',
    title: 'Settlement Timeline',
    description: 'Key dates from filing to payment'
  };
}

return {
  json: {
    articleHtml: articleHtml,
    articleType: 'settlement',
    defendant: formData.Defendant_Name,
    caseNumber: formData.Case_Number,
    ctaType: 'email_signup',
    pdfUrl: formData.pdfUrl || null,  // Will be set if PDF uploaded
    customInstructions: formData.Custom_Instructions || '',
    infographic: infographic,
    metaData: {
      defendant: formData.Defendant_Name,
      caseNumber: formData.Case_Number,
      articleType: 'settlement',
      hasInfographic: true,
      hasFAQ: true,
      hasPDF: !!formData.pdfUrl
    }
  }
};

