// Cleanup HTML output - remove markdown blocks and explanatory text
let cleanHtml = $input.item.json.content[0].text;

// Remove markdown code blocks (```html and ```)
cleanHtml = cleanHtml.replace(/```html\s*/gi, '').replace(/```\s*/g, '');

// Remove common preamble phrases that Claude might add
const preambles = [
  /^Here is the modified HTML:?\s*/i,
  /^Here's the modified HTML:?\s*/i,
  /^Modified HTML:?\s*/i,
  /^The modified HTML is:?\s*/i,
  /^Here is the HTML with internal links:?\s*/i
];

preambles.forEach(pattern => {
  cleanHtml = cleanHtml.replace(pattern, '');
});

// If there's duplicate content (article appears twice), split and take first instance
if (cleanHtml.length > 15000) {
  const midpoint = Math.floor(cleanHtml.length / 2);
  const endOfFirstArticle = cleanHtml.indexOf('</p>', midpoint);
  if (endOfFirstArticle > 0) {
    cleanHtml = cleanHtml.substring(0, endOfFirstArticle + 4);
  }
}

cleanHtml = cleanHtml.trim();

return { 
  json: { 
    articleHtml: cleanHtml,
    articleType: $('When Executed by Another Workflow').item.json.articleType,
    defendant: $('When Executed by Another Workflow').item.json.defendant,
    caseNumber: $('When Executed by Another Workflow').item.json.caseNumber,
    ctaType: $('When Executed by Another Workflow').item.json.ctaType,
    metaData: $('When Executed by Another Workflow').item.json.metaData
  } 
};

// Pass through original HTML for articles that don't need linking
return {
  json: {
    articleHtml: $input.item.json.articleHtml,
    articleType: $input.item.json.articleType,
    defendant: $input.item.json.defendant,
    caseNumber: $input.item.json.caseNumber,
    ctaType: $input.item.json.ctaType,
    metaData: $input.item.json.metaData
  }
};

